uint32_t epd_wave_table_get_frames(int temperature, EpdDrawMode mode)
{
    uint32_t total_frames = 0;

    if (reflesh_times % PART_DISP_TIMES == 0)
    {
        total_frames = sizeof(wave_forms_full) / sizeof(wave_forms_full[0]); //Full refresh
        p_current_wave_from = &wave_forms_full[0][0];
    }
    else
    {
        total_frames = sizeof(wave_forms_partial) / sizeof(wave_forms_partial[0]); //Partial refresh
        p_current_wave_from = &wave_forms_partial[0][0];
    }
    reflesh_times++;



    return total_frames;
}

void epd_wave_table_fill_lut(uint32_t *p_epic_lut, uint32_t frame_num)
{

    const uint8_t *p_frame_wave = p_current_wave_from + (frame_num * 256);

    //Convert the 8-bit waveforms to 32-bit epic LUT values
    for (uint16_t i = 0; i < 256; i++)
        p_epic_lut[i] = p_frame_wave[i] << 3;
}


